import React, { useState } from "react";
import { motion } from "framer-motion";
import { ChevronLeft, ChevronRight } from "lucide-react";

const images = [
    {
        src: "data:image/jpeg;base64,",
        title: "Explore the Cosmos",
        description: "Discover the breathtaking beauty of space.",
    },
    {
        src: "data:image/jpeg;base64,",
        title: "Galactic Wonders",
        description: "Witness the vastness of galaxies.",
    },
    {
        src: "data:image/jpeg;base64,",
        title: "Starry Nights",
        description: "Gaze upon endless constellations.",
    },
];

const Carousel = () => {
    const [currentIndex, setCurrentIndex] = useState(0);

    const handleNext = () => {
        setCurrentIndex((currentIndex + 1) % images.length);
    };

    const handlePrev = () => {
        setCurrentIndex((currentIndex - 1 + images.length) % images.length);
    };

    return (
        <div className="relative w-full max-w-5xl mx-auto p-4">
            {/* Carousel Container */}
            <div className="overflow-hidden rounded-2xl shadow-xl">
                <motion.div
                    key={currentIndex + 1}
                    initial={{ opacity: 0, x: 100 }}
                    animate={{ opacity: 1, x: 0 }}
                    exit={{ opacity: 0, x: -100 }}
                    transition={{ duration: 0.6 }}
                >
                    <img
                        src={images[currentIndex].src}
                        alt={images[currentIndex].title}
                        className="w-full object-cover h-72 md:h-96"
                    />
                </motion.div>
                <div className="absolute inset-0 flex flex-col justify-end bg-gradient-to-t from-black via-transparent to-transparent p-4">
                    <motion.h2
                        className="text-white text-3xl font-bold mb-2"
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.4 }}
                    >
                        {images[currentIndex].title}
                    </motion.h2>
                    <motion.p
                        className="text-gray-300 text-base mb-4"
                        initial={{ opacity: 0, y: 10 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.5 }}
                    >
                        {images[currentIndex].description}
                    </motion.p>
                    <button className="bg-gradient-to-r from-cyan-500 to-blue-500 text-white hover:opacity-90 px-4 py-2">
                        Learn More
                    </button>
                </div>
            </div>

            {/* Navigation Arrows */}
            <button
                className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-black bg-opacity-40 p-2 rounded-full text-white hover:bg-opacity-70"
                onClick={handlePrev}
            >
                <ChevronLeft size={24} />
            </button>
            <button
                className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-black bg-opacity-40 p-2 rounded-full text-white hover:bg-opacity-70"
                onClick={handleNext}
            >
                <ChevronRight size={24} />
            </button>

            {/* Dot Indicators */}
            <div className="flex justify-center space-x-2 mt-4">
                {images.map((_, index) => (
                    <motion.div
                        key={index}
                        className={`h-3 w-3 rounded-full cursor-pointer ${currentIndex === index ? "bg-blue-500" : "bg-gray-400"
                            }`}
                        onClick={() => setCurrentIndex(index)}
                        whileHover={{ scale: 1.2 }}
                    />
                ))}
            </div>
        </div>
    );
};

export default Carousel;
